import React, { useState, useEffect } from 'react';
import SubjectService from './service/SubjectService';

function AdminSubjectManagementPage() {
  const [subjects, setSubjects] = useState([]);
  const [newSubject, setNewSubject] = useState('');

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      const token = localStorage.getItem('token'); 
      const response = await SubjectService.getAllSubjects(token);
      setSubjects(response);
    } catch (error) {
      console.error('Error fetching subjects:', error);
    }
  };

  const handleAddSubject = async () => {
    try {
      const token = localStorage.getItem('token'); 
      await SubjectService.addSubject(newSubject, token);
      setNewSubject('');
      fetchSubjects();
    } catch (error) {
      console.error('Error adding subject:', error);
    }
  };

  return (
    <div className="profile-page-container">
      <h2>Subject Management Page</h2>
      <div>
        <div><input type="text" value={newSubject} onChange={(e) => setNewSubject(e.target.value)} /></div>
        <div>
        <input type="date" value={newSubject} onChange={(e) => setNewSubject(e.target.value)}/>
        </div>
       
        <button onClick={handleAddSubject}>Add Subject</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {subjects.map(subject => (
            <tr key={subject.id}>
              <td>{subject.id}</td>
              <td>{subject.name}</td>
              <td>{subject.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminSubjectManagementPage;
