import React, { useState, useEffect } from 'react';
import SubjectService from './service/SubjectService';

function AdminSubjectManagementPage() {
  const [subjects, setSubjects] = useState([]);
  const [newSubject, setNewSubject] = useState('');

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      const token = localStorage.getItem('token'); 
      const response = await SubjectService.getAllSubjects(token);
      setSubjects(response);
    } catch (error) {
      console.error('Error fetching subjects:', error);
    }
  };

  const handleAddSubject = async () => {
    try {
      const token = localStorage.getItem('token'); 
      await SubjectService.addSubject(newSubject, token);
      setNewSubject('');
      fetchSubjects();
    } catch (error) {
      console.error('Error adding subject:', error);
    }
  };

  const deleteSubject = async (subjectId) => {
    try {
      const confirmDelete = window.confirm('Are you sure you want to delete this subject?');
      if (confirmDelete) {
        const token = localStorage.getItem('token'); 
        await SubjectService.deleteSubject(subjectId, token);
        fetchSubjects();
      }
    } catch (error) {
      console.error('Error deleting subject:', error);
    }
  };

  return (
    <div className="profile-page-container">
      <h2>Subject Management Page</h2>
      <div>
        <input type="text" value={newSubject} onChange={(e) => setNewSubject(e.target.value)} />
        <button onClick={handleAddSubject}>Add Subject</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {subjects.map(subject => (
            <tr key={subject.id}>
              <td>{subject.id}</td>
              <td>{subject.name}</td>
              <td>
                <button className='delete-button' onClick={() => deleteSubject(subject.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminSubjectManagementPage;
