import React, { useState, useEffect } from 'react';
import FileService from './service/FileService';

function FileManagementPage() {
  const [files, setFiles] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchFiles();
  }, []);

  const fetchFiles = async () => {
    try {
      const token = localStorage.getItem('token'); 
      const response = await FileService.getAllFiles(token);
      setFiles(response);
      setError(null); // Clear any previous errors
    } catch (error) {
      console.error('Error fetching files:', error);
      setError('Failed to fetch files. Please try again.'); // Set error state
    }
  };

  const downloadFile = async (downloadURL, fileName) => {
    try {
      const response = await fetch(downloadURL);
      const blob = await response.blob();
      
      // Create a temporary link element
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName);
      
      // Append the link to the document body and trigger the download
      document.body.appendChild(link);
      link.click();
      
      // Clean up
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading file:', error);
      setError('Failed to download file. Please try again.');
    }
  };

  return (
    <div className="profile-page-container">
      <h2>File Management Page</h2>
      {error && <div className="error-message">{error}</div>} {/* Display error message if any */}
      <table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>Download Link</th>
          </tr>
        </thead>
        <tbody>
          {files.map(file => (
            <tr key={file.id}>
              <td>{file.fileName}</td>
              <td>
                {/* Use the downloadURL property from the file object */}
                <button onClick={() => downloadFile(file.downloadURL, file.fileName)}>Download</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FileManagementPage;
