import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import UserService from '../service/UserService';

function Navbar() {
    const [activeLink, setActiveLink] = useState(null); // State to track active link
    const isAuthenticated = UserService.isAuthenticated();
    const isAdmin = UserService.isAdmin();

    const handleLogout = () => {
        const confirmDelete = window.confirm('Are you sure you want to logout this user?');
        if (confirmDelete) {
            UserService.logout();
        }
    };

    const handleLinkClick = (link) => {
        setActiveLink(link); // Set active link when clicked
    };

    return (
        <nav>
            <div className="navbar-logo"></div> {/* Logo container */}
            <ul>
                {!isAuthenticated && <li><Link to="/" onClick={() => handleLinkClick("login")} className={activeLink === "login" ? "active" : ""}>Login</Link></li>}
                {!isAuthenticated && <li><Link to="/register" onClick={() => handleLinkClick("register")} className={activeLink === "register" ? "active" : ""}>Register</Link></li>}
                {isAuthenticated && <li><Link to="/profile" onClick={() => handleLinkClick("profile")} className={activeLink === "profile" ? "active" : ""}>Profile</Link></li>}
                {isAuthenticated && <li><Link to="/files" onClick={() => handleLinkClick("files")} className={activeLink === "files" ? "active" : ""}>File</Link></li>}
                {isAuthenticated && <li><Link to="/upload-file" onClick={() => handleLinkClick("upload-file")} className={activeLink === "upload-file" ? "active" : ""}>UploadFile</Link></li>}
                {isAdmin && <li><Link to="/admin/user-management" onClick={() => handleLinkClick("user-management")} className={activeLink === "user-management" ? "active" : ""}>User Management</Link></li>}
                {isAdmin && <li><Link to="/admin/subject-management" onClick={() => handleLinkClick("subject-management")} className={activeLink === "subject-management" ? "active" : ""}>Subject Management</Link></li>}
                {isAuthenticated && <li><Link to="/" onClick={handleLogout}>Logout</Link></li>}
            </ul>
        </nav>
    );
}

export default Navbar;
