import React, { useState, useEffect } from 'react';
import SubjectService from './service/SubjectService';
import FileService from '../service/FileManagementPage';

function UserFileUploadPage() {
  const [subjects, setSubjects] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [file, setFile] = useState(null);

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      const token = localStorage.getItem('token'); 
      const response = await SubjectService.getAllSubjects(token);
      setSubjects(response.subjects);
    } catch (error) {
      console.error('Error fetching subjects:', error);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    try {
      const token = localStorage.getItem('token'); 
      const formData = new FormData();
      formData.append('file', file);
      formData.append('subject', selectedSubject);
      await FileService.uploadFile(formData, token);
      alert('File uploaded successfully');
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="user-file-upload-container">
      <h2>File Upload Page</h2>
      <div>
        <select value={selectedSubject} onChange={(e) => setSelectedSubject(e.target.value)}>
          <option value="">Select Subject</option>
          {subjects.map(subject => (
            <option key={subject.id} value={subject.id}>{subject.name}</option>
          ))}
        </select>
      </div>
      <div>
        <input type="file" onChange={handleFileChange} />
      </div>
      <button onClick={handleUpload}>Upload File</button>
    </div>
  );
}

export default UserFileUploadPage;
