import React, { useState, useEffect } from 'react';
import FileService from './service/FileService';

function FileManagementPage() {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    fetchFiles();
  }, []);

  const fetchFiles = async () => {
    try {
      const token = localStorage.getItem('token'); 
      const response = await FileService.getAllFiles(token);
      setFiles(response);
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  };

  const downloadFile = async (downloadURL) => {
    try {
      window.open(downloadURL, '_blank');
    } catch (error) {
      console.error('Error downloading file:', error);
    }
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const uploadFile = async () => {
    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      formData.append('file', selectedFile);
      await FileService.uploadFile(formData, token);
      // After successful upload, fetch files again to update the list
      fetchFiles();
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="profile-page-container">
      <h2>File Management Page</h2>
      <div>
        <input type="file" onChange={handleFileChange} />
        <button onClick={uploadFile}>Upload</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>Download Link</th>
          </tr>
        </thead>
        <tbody>
          {files.map(file => (
            <tr key={file.id}>
              <td>{file.fileName}</td>
              <td>
                <button onClick={() => downloadFile(file.downloadURL)}>Download</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default FileManagementPage;
